#include <stdio.h>
#include "../third-party/sds/sds.h"

int main(void)
{
    // SDS_TYPE_5 (length is in the header)
    sds sds_string = sdsnew("Hello World!");
    printf("%s\nlength %ld\n", sds_string, sdslen(sds_string));
    sdsfree(sds_string);

    // SDS_TYPE_16 (length is not in the header)
    sds sds_string_long = sdsnew("");
    printf("%s\nlength %ld\n", sds_string_long, sdslen(sds_string_long));
    sdsfree(sds_string_long);

    return 0;
}
